CREATE TABLE IF NOT EXISTS "books" (
    "bookID" INT,
    "title" TEXT,
    "authors" TEXT,
    "average_rating" NUMERIC(5,2),
    "isbn" TEXT,
    "language_code" TEXT,
    "num_pages" INT,
    "ratings_count" INT,
    "text_reviews_count" INT,
    "publication_date" TIMESTAMP,
    "publisher" TEXT
);
